import React, { Component } from "react";
import paginateData from "../../common/paginate data";
import Like from "../../common/like";
import Pagination from "../../common/pagination";

class MoviesList extends Component {
  state = {
    movies: [{ title: "mai" }, { title: "mai2" }],
    pageSize: 3,
    currentPage: 1,
  };

  handleDelete(ele) {
    const movies = this.state.movies.filter((m) => m._id !== ele._id);
    this.setState({ movies: movies });
  }

  handleLiked(movie) {
    const movies = [...this.state.movies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movies[index] };
    movies[index].liked = !movies[index].liked;
    this.setState({ movies: movies });
  }

  handleChange = (page) => this.setState({ currentPage: page });

  render() {
    const { movies, pageSize, currentPage } = this.state;
    if (this.state.movies.length === 0)
      return <h1>there is no movies in database</h1>;
    const moviesList = paginateData(movies, pageSize, currentPage);
    return (
      <React.Fragment>
        <h1>there are {this.state.movies.length} movies here </h1>
        <table className="table">
          <thead>
            <tr>
              <th>TITLE</th>
              <th>GENRE</th>
              <th>N.STOCK</th>
              <th>RATE</th>
              <th> </th>
              <th> </th>
            </tr>
          </thead>

          <tbody>
            {moviesList.map((movie) => (
              <tr key={movie._id}>
                <td>{movie.title} </td>
                <td>{movie.title} </td>
                <td>{movie.title} </td>
                <td>{movie.title} </td>
                <td>
                  {" "}
                  <Like
                    onClick={() => this.handleLiked(movie)}
                    liked={movie.liked}
                  />{" "}
                </td>
                <td>
                  <i
                    id="trashIcon"
                    style={{ curser: "pointer" }}
                    title="delete"
                    class="fa fa-trash fa-lg "
                    onClick={() => this.handleDelete(movie)}
                  ></i>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <Pagination
          itemsCount={movies.length}
          pageSize={pageSize}
          currentPage={currentPage}
          onPageChange={this.handleChange}
        />
      </React.Fragment>
    );
  }
}

export default MoviesList;
