import React, { useState } from "react";
import Card from "../../common/card";
import { Row, Col, Container } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "./movie.css";
import MovieModal from "../../common/modal";

function Movies(props) {
  const [showModal, setShowModal] = useState(false);
  const [movieName, setMovieName] = useState(null);
  const movies = [
    { name: "mai", id: 1, img: "../../../images/movie.jpg" },
    { name: "mm", id: 2, img: "../../../images/movie.jpg" },
    { name: "mm", id: 3, img: "../../../images/movie.jpg" },
    { name: "mm", id: 4, img: "../../../images/movie.jpg" },
    { name: "mm", id: 5, img: "../../../images/movie.jpg" },
    { name: "mm", id: 6, img: "../../../images/movie.jpg" },
    { name: "mm", id: 7, img: "../../../images/movie.jpg" },
    { name: "mm", id: 8, img: "../../../images/movie.jpg" },
  ];
  const handleShow = (movieName) => {
    setShowModal(true);
    setMovieName(movieName);
  };


  return (
    <Container className="movies">
      <Row>
        {movies.map((movie) => (
          <Col xs={6} lg={3}>
            <div className="card-div" key={movie.id}>
              <Card
                imgSrc={movie.img}
                movieName={movie.name}
                movieStory={movie.name}
                onClick={() => handleShow(movie.name)}
              />
            </div>
          </Col>
        ))}
        <MovieModal
          show={showModal}
          onHide={() => setShowModal(false)}
          ///////////////////////
          name={movieName}
        />
      </Row>
    </Container>
  );
}

export default Movies;
