import { useForm } from "react-hook-form";
import "./form.css";

function Form(props) {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  return (
    <>
      <div className="mainDivForLoginScreen">
        <div className="login-content">
          <h1 className="login-title">Welcome to our Cinema</h1>
          <p className="note"> (For now write any email and password)</p>
        </div>
        <div className="form-box">
          <form onSubmit={handleSubmit((data) => console.log(data))}>
            <div className="form-inputs">
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={account.email || ""}
                onChange={handleChange}
              />
              {errors.email && (
                <div className="alert alert-danger">{errors.email}</div>
              )}
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={account.password || ""}
                onChange={handleChange}
              />
              {errors.password && (
                <div className="alert alert-danger">{errors.password}</div>
              )}
            </div>
            <input
              {...register("email", { required: true, type: "password" })}
            />
            {errors.email && (
              <p className="alert alert-danger">Email is required.</p>
            )}
            <input {...register("password", { required: true })} />
            {errors.password && (
              <p className="alert alert-danger">Password is required.</p>
            )}
            <input type="submit" />
          </form>
        </div>
      </div>
    </>
  );
}

export default Form;
