import "remixicon/fonts/remixicon.css";

import "./fixed nav.css";

function FixedNav(props) {
  function getTodayDate() {
    const string = new Date().toISOString().slice(0, 10);
    return string;
  }

  const handleLogout = () => {
    console.log("logout", sessionStorage.getItem("email"));
    sessionStorage.clear();
  };

  return (
    <>
      <div className="fixedNav">
        <ul>
          <div className="container">
            <div className="row">
              <li className="col-lg-4 ">
                <Link to="/home">
                  <img
                    className="App-logo"
                    src="../../../images/simple.jpg"
                    alt="logo"
                  />
                </Link>
              </li>
              <li className="col-lg-4" id="date">
                Today : {getTodayDate()}
              </li>
              <li className="col-lg-4">mai</li>
            </div>
          </div>
        </ul>
      </div>

      {/* <div className="fixedNav">
