import axios from "axios";

export const handleGetAllMovies = async () => {
  const URL =
    "https://api.themoviedb.org/3/movie/popular?api_key=7a1c19ea3c361a4d3cc53eb70ef8298c";
  const response = await axios
    .get(URL)
    .then((response) => {
      if (response) return response;
    })
    .catch((error) => {
      if (error.response) return error.response;
    });
  return response.data.results;
};
////////////////////
export const handleEditMovie = async (id, data) => {
  const URL =
    "https://api.themoviedb.org/3/movie/popular?api_key=7a1c19ea3c361a4d3cc53eb70ef8298c";
  const response = await axios.patch(URL + "/" + id, { params: data });
  return response.data.results;
};
/////////////
export const handleRemoveMovie = async (id) => {
  const URL =
    "https://api.themoviedb.org/3/movie/popular?api_key=7a1c19ea3c361a4d3cc53eb70ef8298c";
  const response = await axios.delete(URL + "/" + id);
  return response.data.results;
};
