import { useForm } from "react-hook-form";

function Form(props) {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  return (
    <form onSubmit={handleSubmit((data) => console.log(data))}>
      <input {...register("email")} />
      {errors.email && <p className="alert alert-danger">Email is required.</p>}
      <input {...register("password")} />
      {errors.password && <p>Password is required.</p>}
      <input type="submit" />
    </form>
  );
}

export default Form;
