import React, { useState, useEffect } from "react";
import Icon from "../../common/icon";
import "./moviesList.css";
import AddBox from "../../common/add box";
import { Row, Col } from "react-bootstrap";
import * as services from "../../services/api";

function MoviesList(props) {
  const [name, setName] = useState("");
  const [id, setId] = useState(null);

  const arr = [
    { name: "mai", id: 1 },
    { name: "mm", id: 2 },
    { name: "mm", id: 3 },
    { name: "mm", id: 4 },
    { name: "mm", id: 5 },
    { name: "mm", id: 6 },
    { name: "mm", id: 7 },
    { name: "mm", id: 8 },
  ];
  const [movies, setMovies] = useState(arr);

  const [allMovies, setAllMovies] = useState([]);

  useEffect(() => {
    async function GetMovies() {
      const response = await services.handleGetAllMovies();
      setAllMovies(response);
    }
    GetMovies();
  });

  ///////////////////////
  const handleEdit = ({ id, name }) => {
    document.getElementById("add-box").style.display = "block";
    setName(name);
    setId(id);
  };

  const handleSubmit = async () => {
    console.log(name);
    document.getElementById("add-box").style.display = "none";
    const data = { title: name };
    console.log(name, id);
    await services.handleEditMovie(id, data);
    this.useEffect();
  };
  const handleHideAddBox = () => {
    document.getElementById("add-box").style.display = "none";
  };

  return (
    <>
      <Row>
        <Col xs={12}>
          <AddBox
            id="add-box"
            header="Edit Movie Name"
            onSubmit={() => handleSubmit()}
            onCancel={handleHideAddBox}
          >
            <p className="note">
              {" "}
              Note: this Api doesn't allow to edit data, but the code is correct
              '
            </p>
            <input
              type="text"
              placeholder="write another name"
              name="name"
              value={name || ""}
              onChange={(e) => setName(e.target.value)}
            />
          </AddBox>
        </Col>
      </Row>
      {/* /////////////////////////////////////// */}
      <div className="moviesList">
        <h1>there are {movies.length} movies here </h1>
        <div className="table-div">
          <table>
            <thead>
              <tr>
                <th>TITLE</th>
                <th>GENRE</th>
                <th>N.STOCK</th>
                <th>RATE</th>
                <th>Delete </th>
                <th>Edit </th>
              </tr>
            </thead>

            <tbody>
              {allMovies.map((movie) => (
                <tr key={movie.id}>
                  <td>{movie.title} </td>
                  <td>{movie.title} </td>
                  <td>{movie.title} </td>
                  <td>{movie.title} </td>
                  <td className="icons">
                    <Icon
                      id="trashIcon"
                      title="delete"
                      className="ri-delete-bin-line"
                      onClick={() =>
                        setMovies(movies.filter((m) => m.id !== movie.id))
                      }
                    />
                  </td>
                  <td className="icons">
                    <Icon
                      id="arrowIcon"
                      title="edit this name"
                      className="ri-edit-line"
                      onClick={() =>
                        handleEdit({ id: movie.id, name: movie.title })
                      }
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
}

export default MoviesList;
