import React from "react";
import { Link } from "react-router-dom";
import "remixicon/fonts/remixicon.css";

import "./fixed nav.css";

function FixedNav(props) {
  function getTodayDate() {
    const string = new Date().toISOString().slice(0, 10);
    return string;
  }

  const handleLogout = () => {
    console.log("logout", sessionStorage.getItem("email"));
    sessionStorage.clear();
  };

  return (
    <>
      <div className="fixedNav">
        <ul>
          <div className="row">
            {/* <li>
              <span className="menu">
                <i
                  className="ri-menu-line menu-icon"
                  onClick={props.handleSideNav}
                ></i>
              </span>
            </li> */}
            <li className="col-lg-4  ">
              <Link to="/home">
                <img
                  className="App-logo"
                  src="../../../images/simple.jpg"
                  alt="logo"
                />
              </Link>
            </li>
            <li className="col-lg-4  " id="date">
              Today : {getTodayDate()}
            </li>
            <li className="col-lg-4  logout">
              <span>
                <i className="ri-logout-box-line"></i>
              </span>
              <Link
                className="logout"
                to="/login"
                onClick={() => handleLogout()}
              >
                Logout
              </Link>
            </li>
          </div>
        </ul>
      </div>
    </>
  );
}

export default FixedNav;
