import React from "react";
import Button from "./button";

function Card(props) {
  return (
    <>
      <div className="card">
        <img src={props.imgSrc} alt="movie's img" />
        <div className="content">
          <h3>{props.movieName}</h3>
          <p>{props.movieStory}</p>
        </div>
        <Button
          onClick={props.onClick}
          class="readMore  btn btn-primary"
          buttonContent="Read More ..."
        />
      </div>
    </>
  );
}

export default Card;
