import React, { useState, UseContext } from "react";
import Joi from "joi-browser";
import "./login.css";

function LoginScreen(props) {
  const [account, setAccount] = useState({
    email: "",
    password: "",
  });
  const [errors, setErrors] = useState({});
  /*/////////////// functions ////////////////*/
  const handleChange = (e) => {
    const newAccountState = {
      ...account,
      [e.target.name]: e.target.value,
    };
    setAccount(newAccountState);
  };

  const schema = {
    email: Joi.string().required(),
    password: Joi.string().required(),
  };

  const validate = () => {
    //clone
    const errors = {};
    //edit
    const result = Joi.validate(account, schema);
    if (result.error === null) {
      setErrors({});
      return null;
    }
    for (const error of result.error.details) {
      errors[error.path] = error.message;
    }
    //setState
    setErrors(errors);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const errors = validate();
    if (errors) return;
    else {
      sessionStorage.setItem("email", account.email || "");
      window.location = "/movies";
    }
  };

  return (
    <React.Fragment>
      <div className="mainDivForLoginScreen">
        <div className="login-content">
          <h1 className="login-title">Welcome to our Cinema</h1>
          <p className="note"> (For now write any email and password)</p>
        </div>
        <div className="form-box">
          <form action="post" onSubmit={handleSubmit}>
            <div className="form-inputs">
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={account.email || ""}
                onChange={handleChange}
              />
              {errors.email && (
                <div className="alert alert-danger">{errors.email}</div>
              )}
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={account.password || ""}
                onChange={handleChange}
              />
              {errors.password && (
                <div className="alert alert-danger">{errors.password}</div>
              )}
            </div>
            <div className="check-div">
              <div className="checkbox-divContainer"></div>
            </div>
            <button>Submit</button>
          </form>
        </div>
      </div>
    </React.Fragment>
  );
}

export default LoginScreen;
