import React, { useState, useEffect } from "react";
import Card from "../../common/card";
import { Row, Col, Container } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "./movie.css";
import MovieModal from "../../common/modal";
import { handleGetAllMovies } from "../../services/api";

function Movies(props) {
  const [showModal, setShowModal] = useState(false);
  const [modalData, setModalData] = useState({});
  const [allMovies, setAllMovies] = useState([]);

  useEffect(() => {
    async function GetMovies() {
      const response = await handleGetAllMovies();
      setAllMovies(response);
    }
    GetMovies();
  });

  const handleShow = ({ name, language, date, overview }) => {
    setShowModal(true);
    setModalData({
      name: name,
      language: language,
      overview: overview,
      date: date,
    });
  };

  return (
    <Container className="movies">
      <Row>
        {allMovies.map((movie) => (
          <Col xs={6} lg={3}>
            <div className="card-div" key={movie.id}>
              <Card
                imgSrc={movie.backdrop_path}
                movieName={movie.title}
                onClick={() =>
                  handleShow({
                    name: movie.title,
                    overView: movie.overview,
                    language: movie.original_language,
                    date: movie.release_date,
                  })
                }
              />
            </div>
          </Col>
        ))}
        <MovieModal
          show={showModal}
          onHide={() => setShowModal(false)}
          ///////////////////////
          name={modalData.name}
          language={modalData.language}
          date={modalData.date}
          overview={modalData.overview}
        />
      </Row>
    </Container>
  );
}

export default Movies;
