import React, { useState } from "react";
import Icon from "../../common/icon";

import Pagination from "../../common/pagination";

function MoviesList(props) {
  const pageSize = 3;
  const [currentPage, setCurrentPage] = useState(1);
  const arr = [
    { name: "mai", id: 1 },
    { name: "mm", id: 2 },
  ];
  const [movies, setMovies] = useState(arr);

  //   const handleDelete = (ele) => {
  //     setmovies = movies.filter((m) => m._id !== ele._id);
  //   };

  // const handleChange = (page) => this.setState({ currentPage: page });

  return (
    <>
      <h1>there are {movies.length} movies here </h1>
      <table className="table">
        <thead>
          <tr>
            <th>TITLE</th>
            <th>GENRE</th>
            <th>N.STOCK</th>
            <th>RATE</th>
            <th> </th>
            <th> </th>
          </tr>
        </thead>

        <tbody>
          {movies.map((movie) => (
            <tr key={movie.id}>
              <td>{movie.name} </td>
              <td>{movie.name} </td>
              <td>{movie.name} </td>
              <td>{movie.name} </td>

              <td>
                <Icon
                  id="trashIcon"
                  title="delete"
                  className="ri-delete-bin-line"
                  onClick={() =>
                    setMovies(movies.filter((m) => m._id !== movie._id))
                  }
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <Pagination
        itemsCount={movies.length}
        pageSize={pageSize}
        currentPage={currentPage}
        onPageChange={(page) => setCurrentPage(page)}
      />
    </>
  );
}

export default MoviesList;
