import React from "react";
import Button from "./button";

function Card(props) {

  return (
    <>
      <div className="card">
        <img src={props.imgSrc} alt="movie's img" />
        <h3>{props.movieName}</h3>
        <p>{props.movieStory}</p>
        <Button
          onClick={props.onClick}
          class="readMore"
          buttonContent="Read More"
        />
      </div>
    </>
  );
}

export default Card;
