import React, { useState } from "react";

const Like = (props) => {
  const [iconClass, setIconClass] = useState("ri-heart-line");
  if (!props.liked) {
    seticonClass("ri-heart-fill");
  }
  return (
    <i
      id="heart-icon"
      className={iconClass}
      aria-hidden="true"
      style={{ cursor: "pointer" }}
      onClick={props.onClick}
    >
      {" "}
    </i>
  );
};

export default Like;

