import React, { useState } from "react";
import Like from "../../common/like";
import Pagination from "../../common/pagination";

function MoviesList(props) {
  //   const pageSize = 3;
  //   const [currentPage, setCurrentPage] = (React.useState = 1) | (Number > 1);
  const arr = [
    { name: "mai", id: 1 },
    { name: "mm", id: 2 },
  ];
  const [movies, setMovies] = useState(arr);

  //   const handleDelete = (ele) => {
  //     setmovies = movies.filter((m) => m._id !== ele._id);
  //   };

  const handleLiked = (movie) => {
    const movies = [...movies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movies[index] };
    movies[index].liked = !movies[index].liked;
    setMovies(movies);
  };

  //   const handleChange = (page) => this.setState({ currentPage: page });

  return (
    <>
      <h1>there are {movies.length} movies here </h1>
      <table className="table">
        <thead>
          <tr>
            <th>TITLE</th>
            <th>GENRE</th>
            <th>N.STOCK</th>
            <th>RATE</th>
            <th> </th>
            <th> </th>
          </tr>
        </thead>

        <tbody>
          {movies.map((movie) => (
            <tr key={movie.id}>
              <td>{movie.name} </td>
              <td>{movie.name} </td>
              <td>{movie.name} </td>
              <td>{movie.name} </td>
              <td>
                <Like onClick={() => handleLiked(movie)} liked={movie.liked} />
              </td>
              <td>
                <i
                  id="trashIcon"
                  style={{ curser: "pointer" }}
                  title="delete"
                  class="fa fa-trash fa-lg "
                  onClick={() =>
                    setMovies(movies.filter((m) => m._id !== movie._id))
                  }
                ></i>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {/* <Pagination
        itemsCount={movies.length}
        pageSize={pageSize}
        currentPage={currentPage}
        onPageChange={(page) => setCurrentPage(page)}
      /> */}
    </>
  );
}

export default MoviesList;
