import React, { useState } from "react";
import Icon from "../../common/icon";
import "./moviesList.css";
import Pagination from "../../common/pagination";
import AddBox from "../../common/add box";
import { Row, Col } from "react-bootstrap";

function MoviesList(props) {
  const pageSize = 4;
  const [currentPage, setCurrentPage] = useState(1);
  const [name, setName] = useState("");
  //   const [showModal, setShowModal] = useState(false);
  //   const [modalName, setModalName] = useState("");

  const arr = [
    { name: "mai", id: 1 },
    { name: "mm", id: 2 },
    { name: "mm", id: 3 },
    { name: "mm", id: 4 },
    { name: "mm", id: 5 },
    { name: "mm", id: 6 },
    { name: "mm", id: 7 },
    { name: "mm", id: 8 },
  ];
  const [movies, setMovies] = useState(arr);
  ///////////////////////
  const handleEdit = (id) => {
    console.log("edit", id);
    document.getElementById("add-box").style.display = "block";
  };
  const handleSubmit = () => {
    console.log(name);
    document.getElementById("add-box").style.display = "none";
    setName("");
  };
  const handleHideAddBox = () => {
    document.getElementById("add-box").style.display = "none";
  };

  return (
    <>
      <Row>
        <Col xs={12}>
          <AddBox
            id="add-box"
            header="Edit Movie Name"
            onSubmit={handleSubmit}
            onCancel={handleHideAddBox}
          >
            <input
              type="text"
              placeholder="write another name"
              name="name"
              value={name || ""}
              onChange={(e) => setName(e.target.value)}
            />
          </AddBox>
        </Col>
      </Row>

      <div className="moviesList">
        <h1>there are {movies.length} movies here </h1>
        <div className="table-div">
          <table>
            <thead>
              <tr>
                <th>TITLE</th>
                <th>GENRE</th>
                <th>N.STOCK</th>
                <th>RATE</th>
                <th> </th>
                <th> </th>
              </tr>
            </thead>

            <tbody>
              {movies.map((movie) => (
                <tr key={movie.id}>
                  <td>{movie.name} </td>
                  <td>{movie.name} </td>
                  <td>{movie.name} </td>
                  <td>{movie.name} </td>
                  <td>
                    <Icon
                      id="trashIcon"
                      title="delete"
                      className="ri-delete-bin-line"
                      onClick={() =>
                        setMovies(movies.filter((m) => m.id !== movie.id))
                      }
                    />
                  </td>
                  <td>
                    <Icon
                      id="arrowIcon"
                      title="Show Popup"
                      className="ri-edit-line"
                      onClick={() => handleEdit(movie.id)}
                    />
                  </td>
                </tr>
              ))}
