import React, { useState } from "react";
import Joi from "joi-browser";
import "./login.css";

function LoginScreen(props) {
  const [account, setAccount] = useState({
    email: "",
    password: "",
  });
  const [errors, setErrors] = useState({});

  /*/////////////// functions ////////////////*/
  const handleChange = (e) => {
    const newAccountState = {
      ...account,
      [e.target.name]: e.target.value,
    };
    setAccount(newAccountState);
  };

  const schema = {
    email: Joi.string().required(),
    password: Joi.string().required(),
  };

  const validate = () => {
    //clone
    const errors = {};
    //edit
    const result = Joi.validate(account, schema);
    if (result.error === null) {
      setErrors({ errors: {} });
      return null;
    }
    for (const error of result.error.details) {
      errors[error.path] = error.message;
    }
    //setState
    setErrors({ errors });
  };

  const handleSubmit = (e) => {
    try {
      e.preventDefault();
      const errors = validate();
      if (errors) return;
      /*///get token ////////////////////////////////////////////*/
      async function GetUsers() {
        const base_url = "https://dev.goldady.com/admin-api";
        const url = base_url + `/auth/login`;

        const data = { email: account.email, password: account.password };
        const res = await axios.post(url, data);
        //console.log("res from login ", res.data);

        if (res.data.access_token) {
          const user = res.data.user.first_name + " " + res.data.user.last_name;
          const user_id = res.data.user.id;
          // const token = localStorage.setItem("token", res.data.access_token);
          // localStorage.setItem("user", user);
          // localStorage.setItem("user_id", user_id);
          const token = sessionStorage.setItem("token", res.data.access_token);
          sessionStorage.setItem("user", user);
          sessionStorage.setItem("user_id", user_id);

          //console.log("user is ", localStorage.getItem("user"));
        }
        //console.log(token);
        window.location = "/dashboard";
      }
      GetUsers();
      // ///catch/////////////////////////////////////
    } catch (ex) {
      if (ex.response && ex.response.status === 400) {
        const anyError = errors;
        anyError.email = ex.response.data;
        setErrors({ errors: anyError });
      }
    }
  };

  return (
    <React.Fragment>
      <div className="mainDivForLoginScreen">
        <div className="login-form">
          <div className="form-box">
            <form action="post" onSubmit={handleSubmit}>
              <div className="form-inputs">
                <input
                  type="email"
                  name="email"
                  placeholder="Email"
                  value={account.email}
                  onChange={handleChange}
                />
                {errors.email && (
                  <div className="alert alert-danger">{errors.email}</div>
                )}
                <input
                  type="password"
                  name="password"
                  placeholder="Password"
                  value={account.password}
                  onChange={handleChange}
                />
                {errors.password && (
                  <div className="alert alert-danger">{errors.password}</div>
                )}
              </div>
              <div className="check-div">
                <div className="checkbox-divContainer"></div>
              </div>
              <button>Submit</button>
            </form>
          </div>
        </div>
      </div>
    </React.Fragment>
  );
}

export default LoginScreen;
